import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "./Add.css";

function Add() {
  //   const [name, setName] = useState("");
  const [pledges, setPledges] = useState([]);
  const history = useHistory();

  useEffect(() => {
    fetch("/pledges")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPledges(data);
      })
      .catch((error) => {});
  }, []);

  const handleSubmission = (event) => {
    event.preventDefault();
    fetch("/reveal", {})
      .then((response) => response.json())
      .then((data) => {})
      .catch((error) => {});
  };

  function handleClick() {
    history.push("/");
  }
  return (
    <div>
      <button class="nes-btn is-success margin" onClick={handleClick}>
        Back
      </button>
          <div class="nes-container is-rounded">
            <p class="title" id="welcome">
              Add Pledge
            </p>
            <form class="add_cont" onSubmit={handleSubmission}>
              <div class="row">
                <div class="nes-field">
                  <label for="name_field">Pledge Name</label>
                  <input type="text" id="name_field" class="nes-input"></input>
                </div>
                <div class="nes-select narrow">
                  <label for="name_field">Family</label>
                  <select required id="default_select" class="narrow">
                    <option value="" disabled selected hidden>
                      Family
                    </option>
                    <option value="Tight">Tight</option>
                    <option value="Loose">Loose</option>
                    <option value="Close">Close</option>
                  </select>
                </div>
              </div>
              <button type="button" class="nes-btn is-success add_btn">
                Add Pledge
              </button>
            </form>
            <h2>List of Pledges</h2>
            <div class="nes-table-responsive">
              <table class="nes-table is-bordered is-centered">
                <thead>
                  <tr>
                    <th>Pledge</th>
                    <th>Family</th>
                  </tr>
                </thead>
                <tbody>
                  {pledges.map(({name, fam, i}) => {
                    return (
                      <tr key={i}>
                        <td>{name}</td>
                        <td>{fam}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
    </div>
  );
}

export default Add;
