import "./App.css";

function App() {
  const handleSubmission = (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("cert", selectedFile);
    formData.append("caName", currCA);
    setBeingCreated(true);
    console.log(formData);
    fetch("/sign", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
      })
      .catch((error) => {
      });
  };
  return (
    <div class="middle">
      <div class="center">
        <br></br>
        <div class="nes-container with-title is-centered">
          <p class="title" id="welcome">
            Welcome to Fam Reveal
          </p>
          <form class="container" onSubmit={handleSubmission}>
            <label>Choose your family:</label>
            <div>
              <i class="nes-bulbasaur is-small"></i>
              <i class="nes-charmander is-small"></i>
              <i class="nes-squirtle is-small"></i>
            </div>
            <br></br>
            <div class="nes-field">
              <input
                type="text"
                id="name_field"
                class="nes-input"
                placeholder="Your Name"
                required
              ></input>
              <br></br>
            </div>
            <button type="button" class="nes-btn is-success" type="submit">
              Reveal!
            </button>
          </form>
          <i class="nes-pokeball is-small"></i>
        </div>
      </div>
    </div>
  );
}

export default App;
