import React, { useEffect, useState } from "react";
import "./Home.css";

function Home() {
  const [name, setName] = useState("");
  const [error, setError] = useState(false);

  useEffect(() => {
    setName("");
    setError(false);
  }, []);

  const handleSubmission = (event) => {
    event.preventDefault();
    console.log(name);
    const formData = new FormData();
    formData.append("name", name);
    console.log(formData);
    fetch("/reveal", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        if(error in data){
          setError(true)
        } else{

        }
        console.log(data);
        window.location = data.redirect;
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div class="work">
      <div class="center">
        <div class="nes-container is-rounded">
          <p class="title" id="welcome">
            Welcome to Fam Reveal
          </p>
          <form class="container" onSubmit={handleSubmission}>
            <label>Choose your family:</label>
            <div>
              <i class="nes-bulbasaur is-small"></i>
              <i class="nes-charmander is-small"></i>
              <i class="nes-squirtle is-small"></i>
            </div>
            <br></br>
            <div class="nes-field">
              <input
                type="text"
                class="nes-input"
                placeholder="Your Name"
                onChange={(e) => setName(e.target.value)}
                required
              ></input>
              {error ? (
                <label class="nes-text is-error" id="error">
                  Wrong name. Please try again.
                </label>
              ) : null}
            </div>
            <button type="button" class="nes-btn is-success" type="submit" id="reveal_btn">
              Reveal!
            </button>
            <i class="nes-pokeball is-small"></i>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
