import React, { useState, useEffect } from "react";
import "./Pledges.css";

function Pledges({ pledges }) {
  return (
    <div>
      <div class="row">
        <div class="vertical">
          <h2>List of Pledges</h2>
        </div>
        <button type="button" class="nes-btn is-error" id="delete_all">
          Delete All
        </button>
      </div>
      <div class="row">
        <div class="nes-table-responsive tableFixHead">
          <table class="nes-table is-bordered is-centered">
            <thead>
              <tr>
                <th>Pledge</th>
                <th>Family</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>
              {pledges.map(({ name, fam, i }) => {
                return (
                  <tr key={i} class={fam}>
                    <td>
                      {/* <input
                            class={"name_input " + fam}
                            type="text"
                            value={name}
                            required
                        ></input> */}
                      {i}{name}
                    </td>
                    <td class="center_fam">{fam}</td>
                    <td>
                      <button type="button" class="nes-btn is-error">
                        Delete
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Pledges;
